import '../css/signUp.css';
import logo from '../images/appa_logo.png'
import arrow from '../images/Arrow 1.png'
import { Link } from 'react-router-dom'
import { useState } from "react";
import DatePicker from 'react-datepicker';
import 'react-datepicker/dist/react-datepicker.css';


const SignUp = () => {
    const [formData, setFormData] = useState({
        title: "",
        password: "", 
        first_name: "",
        last_name: "",
        nationality: "",
        postal_code:"",
        city: "",
        district: "",
        country: "",
        profession:"",
        current_organization:"",
        dob:"",
        tel_code: "233",
        telephone: "",
        first_name_next_of_kin: "",
        last_name_next_of_kin: "s",
        year_group1:"",
        chapter1: "",
        house1: "",
        email: "",
        tel_next_of_kin:""
    });

    const handleChange = (e) => {
        const {name, value} = e.target;
        setFormData({...formData, [name]: value});
    };

    const handleSubmit = async (e) => {
        e.preventDefault();

        try {
            const response = await fetch("http://127.0.0.1:8000/api/register/", {
                method: 'POST',
                headers: {'Content-Type': 'application/json',},
                body: JSON.stringify(formData),
            });

            if (!response.ok){
                throw new Error('Registration failed');
            }

            console.log('Registration successful');
        }
        catch(error){
            console.error('Registration error:', error.message)

        }
    };

    return ( 
        <div className="sign-up-container">
            <div className="left">
                <h3> Archbishop Porter Past </h3>
                <h3> Students Association </h3>
                <div className="crest-box">
                    <img src={logo} alt="" />
                </div>
                <div className="login-box">
                    <p className="yellow-arrow">&#8594; </p>
                    <a href="" className='text'> Log In </a>
                </div>
            </div>
            <div className="right">
                <h1> Create an Account </h1>
                <h4> Register Here!</h4>
                <form action=""></form>
            </div>
        </div>
     );
}

export default SignUp;
